// Profile.js
import React, { useState, useContext } from 'react';
import { AppContext } from '../AppContext';
import { useNavigate } from 'react-router-dom';

function Profile({ onCreatePost }) {
  const { addPost } = useContext(AppContext);
  const navigate = useNavigate();

  const [name, setName] = useState('Alexa');
  const [major, setMajor] = useState('Nursing');
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState('');

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleMajorChange = (event) => {
    setMajor(event.target.value);
  };

  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleContentChange = (event) => {
    setContent(event.target.value);
  };

  const handleDescriptionChange = (event) => {
    setDescription(event.target.value);
  };

  const handleImageChange = (event) => {
    setImage(event.target.value);
  };

  const handleCreatePost = (event) => {
    event.preventDefault();
    const newPost = {
      name,
      major,
      title,
      content,
      description,
      image,
    };
    // Pass the new post to the parent component
    onCreatePost(newPost);
    // Navigate to the home page after creating the post
    navigate('/');
  };

  return (
    <div>
      <h2>Create New Post</h2>
      <form onSubmit={handleCreatePost}>
        {/* ...other form fields... */}
        <div className="form-group">
          <button type="submit" className="btn btn-primary">
            Create Post
          </button>
        </div>
      </form>
    </div>
  );
}

export default Profile;
