// UserCard.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const UserCard = ({ name, major, title, content, imageSrc, onSwipeLeft, onSwipeRight }) => {
  const [visible, setVisible] = useState(true);

  const handleSwipeLeft = () => {
    setVisible(false);
    onSwipeLeft();
  };

  const handleSwipeRight = () => {
    setVisible(false);
    onSwipeRight();
  };

  if (!visible) {
    return null;
  }

  return (
    <div className="col-6 col-lg-3">
      <div className="user mb-4">
        <div className="user-body">
          <img className="pb-3 profile-image img-fluid" src={imageSrc} alt={`Profile of ${name}`} />
          <h2>{name}</h2>
          <p>Major: {major}</p>
          <p>Title of Post: {title}</p>
          <p>Content of Post: {content}</p>
          <button className="swipe-button swipe-left" onClick={handleSwipeLeft}>
            Swipe Left
          </button>
          <Link to="/match">
            <button className="swipe-button swipe-right" onClick={handleSwipeRight}>
              Swipe Right
            </button>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default UserCard;
