// Profile.js
import React, { useState } from 'react';

function Profile({ addPost }) {  // Updated prop name here
  const [name, setName] = useState('Alexa');
  const [major, setMajor] = useState('Nursing');
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState('');

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleMajorChange = (event) => {
    setMajor(event.target.value);
  };

  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleContentChange = (event) => {
    setContent(event.target.value);
  };

  const handleDescriptionChange = (event) => {
    setDescription(event.target.value);
  };

  const handleImageChange = (event) => {
    setImage(event.target.value);
  };

  const handleCreatePost = (event) => {
    event.preventDefault();
    const newPost = {
      name,
      major,
      title,
      content,
      description,
      image,
    };
    // Pass the new post to the parent component
    addPost(newPost);  // Updated function name here
  };

  return (
    <div>
      <h2>Create New Post</h2>
      <form onSubmit={handleCreatePost}>
        {/* ... rest of the form ... */}
        <div className="form-group">
          <button type="submit" className="btn btn-primary">Create Post</button>
        </div>
      </form>
    </div>
  );
}

export default Profile;
