import React from 'react';
import './index.css';
import Header from './components/Header';
import Profile from './components/Profile';
import Matches from './components/Matches';
import Users from './components/Users';
import Match from './components/Match';
import Login from './components/Login';
import { Route, Routes } from "react-router-dom";
import { AppProvider } from './AppContext';

function App() {
  const { addPost } = useContext(AppContext);

  const handleCreatePost = (newPost) => {
    // Handle the creation of the post here
    console.log("New Post:", newPost);
    // Add the new post to the context
    addPost(newPost);
  };
  return (
    <AppProvider>
    <div className="App">
      <Header />
      <main>
      <Routes> {/* the collection of routes to match */}
            {/* if currentUrlPath === "home" */}
            <Route path="profile" element={<Profile onCreatePost={handleCreatePost} />} />
            <Route path="login" element={<Login />} />

            {/* if currentUrlPath ===  "about" */}
            <Route path="matches" element={<Matches />} />
            <Route path="/" element={<Users />} />
            <Route path="match" element={<Match />} />
        </Routes>        
      </main>
      <footer>
        <p>&copy; 2023 UW Friend Finder. All rights reserved.</p>
      </footer>
    </div>
    </AppProvider>
  );
}

export default App;
