import React, { useState } from 'react';
import MatchedUserCard from './MatchedUserCard';
import Chat from './Chat';

function Matches() {
  const initialMatchedUsers = [
    { name: 'Jack Brown', age: 21, major: 'Business Administration: Finance', hobbies: 'Golfing, Surfing, Gym', careerAspirations: 'Financial Consulting', img: `images/jb-profile.jpeg`},
    { name: 'Howard Chen', age: 19, major: 'Informatics', hobbies: 'Running, Video Games, Concerts', careerAspirations: 'UX Researcher', img: `images/hc-profile.jpeg`},
    { name: 'Chris Jones', age: 20, major: 'Political Science', hobbies: 'Reading, Swimming, Hiking', careerAspirations: 'Lawyer', img: `images/cj-profile.jpeg`}
  ];

  const [matchedUsers, setMatchedUsers] = useState(initialMatchedUsers);
  const [minAge, setMinAge] = useState('');
  const [activeUser, setActiveUser] = useState(null);
  const [lastSwipedUserId, setLastSwipedUserId] = useState(null);


  const handleFilter = () => {
    const filteredUsers = initialMatchedUsers.filter((user) => {
      return user.age >= Number(minAge);
    });
    setMatchedUsers(filteredUsers);
  };

  const handleUnmatch = (name) => {
    const updatedUsers = matchedUsers.filter((user) => user.name !== name);
    setMatchedUsers(updatedUsers);
  };

  const handleChat = (user) => {
    setActiveUser(user);
  };

  let rearrangedUsers = [...matchedUsers];
  if (lastSwipedUserId) {
    const swipedUserIndex = rearrangedUsers.findIndex(user => user.id === lastSwipedUserId);
    if (swipedUserIndex !== -1) {
      const swipedUser = rearrangedUsers.splice(swipedUserIndex, 1)[0];
      rearrangedUsers = [swipedUser, ...rearrangedUsers];
    }
  }

  const matchedUserCards = matchedUsers.map((user, index) => (
    <MatchedUserCard
      key={index}
      user={user}
      onUnmatch={() => handleUnmatch(user.name)}
      onChat={() => handleChat(user)}
    />
  ));

  const handleMinAgeChange = (event) => {
    setMinAge(event.target.value);
  };

  const handleSwipeRight = (userId) => {
    const updatedUsers = matchedUsers.map((user) => {
      if (user.id === userId) {
        return { ...user, swipedRight: true };
      }
      return user;
    });

    setMatchedUsers(updatedUsers);
    setLastSwipedUserId(userId);
  };

  return (
    <div className="matches-container">
      <div className="profiles-container">
        <h2>Matched Users</h2>
        <form onSubmit={(event) => {
          event.preventDefault();
          handleFilter();
        }}>
          <div>
            <label htmlFor="minAge">Minimum Age:</label>
            <input
              type="number"
              id="minAge"
              name="minAge"
              value={minAge}
              onChange={handleMinAgeChange}
            />
            <button type="submit">Filter</button>
          </div>
        </form>
        {matchedUserCards}
      </div>
      <div className="chat-section">
        {activeUser && <Chat user={activeUser} />}
      </div>
    </div>
  );
}

export default Matches;
