import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const UserCard = ({ name, age, major, hobbies, careerAspirations, imageSrc, onSwipeLeft, onSwipeRight, description }) => {
  const [visible, setVisible] = useState(true);

  const handleSwipeLeft = () => {
    setVisible(false);
    onSwipeLeft();
  };

  const handleSwipeRight = () => {
    setVisible(false);
    onSwipeRight();
  }

  if (!visible) {
    return null;
  }

  return (
    <div className="col-12 col-md-6 col-lg-3">
      <div className="user mb-4 uw-themed-card">
        <div className="user-body">
          <img className="pb-3 profile-image img-fluid" src={imageSrc} alt={`Profile of ${name}`} />
          <h2>{name}</h2>
          <p>Age: {age}</p>
          <p>Major: {major}</p>
          <p>Hobbies: {hobbies}</p>
          <p>Career Aspirations: {careerAspirations}</p>
          <p>{description}</p>
          <div className="button-container">
            <button className="btn btn-danger swipe-button" onClick={handleSwipeLeft}>Don't Care</button>
            <Link to="/match">
              <button className="btn btn-success swipe-button" onClick={handleSwipeRight}>Care</button>
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserCard;
