import React, { useState } from 'react';
import MatchedUserCard from './MatchedUserCard';

function Matches() {
  const initialMatchedUsers = [
    { name: 'Jack Brown', age: 21, major: 'Business Administration: Finance', hobbies: 'Golfing, Surfing, Gym', careerAspirations: 'Financial Consulting', img: `images/jb-profile.jpeg`},
    { name: 'Howard Chen', age: 19, major: 'Informatics', hobbies: 'Running, Video Games, Concerts', careerAspirations: 'UX Researcher', img: `images/hc-profile.jpeg`},
    { name: 'Chris Jones', age: 20, major: 'Political Science', hobbies: 'Reading, Swimming, Hiking', careerAspirations: 'Lawyer', img: `images/cj-profile.jpeg`}
  ];

  const [matchedUsers, setMatchedUsers] = useState(initialMatchedUsers);
  const [minAge, setMinAge] = useState('');

  const handleFilter = () => {
    const filteredUsers = initialMatchedUsers.filter((user) => {
      return user.age >= Number(minAge);
    });
    setMatchedUsers(filteredUsers);
  };

  return (
    <div>
      <h2>Matched Users</h2>
      <form onSubmit={(event) => {
        event.preventDefault();
        handleFilter();
      }}>
        <div>
          <label htmlFor="minAge">Minimum Age:</label>
          <input type="number" id="minAge" name="minAge" value={minAge} onChange={(event) => setMinAge(event.target.value)} />
          <button type="submit">Filter</button>
        </div>
      </form>
      {matchedUsers.map((user, index) => (
        <MatchedUserCard key={index} user={user} />
      ))}
    </div>
  );
}

export default Matches;
