// Profile.js
import React, { useState, useContext } from 'react';
import { AppContext } from '../AppContext';

function Profile({ onCreatePost }) {
  const { addPost } = useContext(AppContext);
  const [name, setName] = useState('Alexa');
  const [major, setMajor] = useState('Nursing');
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState(''); // Store the image file as a base64 string

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleMajorChange = (event) => {
    setMajor(event.target.value);
  };

  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleContentChange = (event) => {
    setContent(event.target.value);
  };

  const handleDescriptionChange = (event) => {
    setDescription(event.target.value);
  };

  const handleImageChange = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      setImage(reader.result);
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleCreatePost = (event) => {
    event.preventDefault();
    const newPost = {
      name,
      major,
      title,
      content,
      description,
      image,
    };
    // Pass the new post to the parent component
    onCreatePost(newPost);
  };

  return (
    <div>
      <h2>Create New Post</h2>
      <form onSubmit={handleCreatePost}>
        {/* ... other form inputs ... */}
        <div className="form-group">
          <label htmlFor="image">Image Upload:</label>
          <input type="file" id="image" name="image" onChange={handleImageChange} accept="image/*" />
        </div>
        <div className="form-group">
          <button type="submit" className="btn btn-primary">
            Create Post
          </button>
        </div>
      </form>
    </div>
  );
}

export default Profile;
