import React, { useState } from 'react';
import UserCard from './UserCard';

const Users = ({ handleSwipeRightOnUsers, handleAddToMatches }) => {
  const usersData = [
    {
      name: 'Diego Garcia',
      age: 21,
      major: 'Computer Science',
      hobbies: 'Gaming, Traveling',
      careerAspirations: 'Software Engineering',
      imageSrc: `images/diego-garcia-profile.jpg`,
      visible: true,
    },
    {
      name: 'Ayesha Khan',
      age: 20,
      major: 'Psychology',
      hobbies: 'Reading, Painting',
      careerAspirations: 'Medical Consulting',
      imageSrc: `images/ayesha-khan-profile.jpg`,
      visible: true,
    },
    {
      name: 'John Davis',
      age: 22,
      major: 'Business Administration',
      hobbies: 'Guitar, Photography',
      careerAspirations: 'Entrepreneurship and Start-ups',
      imageSrc: `images/john-davis-profile.jpg`,
      visible: true,
    },
    {
      name: 'Emma Smith',
      age: 19,
      major: 'Biology',
      hobbies: 'Hiking, Cooking',
      careerAspirations: 'Clinical or Medical Research',
      imageSrc: `images/emma-smith-profile.jpg`,
      visible: true,
    },
  ];

  const [users, setUsers] = useState(usersData);

  const handleSwipeLeft = (index) => {
    const newUsers = [...users];
    newUsers[index].visible = false;
    setUsers(newUsers);
  };

  const userCards = users.map((user, index) => (
    <UserCard
      key={user.name}
      {...user}
      onSwipeLeft={() => handleSwipeLeft(index)}
      onSwipeRight={() => handleSwipeRightOnUsers(user)}
    />
  ));

  return (
    <div className="container">
      <div className="row">
        {userCards}
      </div>
    </div>
  );
};

export default Users;
