import React, { useState } from 'react';

function Profile() {
  const [age, setAge] = useState(21);
  const [major, setMajor] = useState('Nursing');
  const [hobbies, setHobbies] = useState('Traveling, Baking, Surfing, TikTok');
  const [careerAspirations, setCareerAspirations] = useState('Travel Nurse');

  const handleAgeChange = (event) => {
    setAge(event.target.value);
  };

  const handleMajorChange = (event) => {
    setMajor(event.target.value);
  };

  const handleHobbiesChange = (event) => {
    setHobbies(event.target.value);
  };

  const handleCareerAspirationsChange = (event) => {
    setCareerAspirations(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("Updated Age:", age);
    console.log("Updated Major:", major);
    console.log("Updated Hobbies:", hobbies);
    console.log("Updated Career Aspirations:", careerAspirations);
  };

  return (
    <div>
      <h2>User Profile</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="age">Age:</label>
          <input type="text" id="age" name="age" value={age} onChange={handleAgeChange} />
          <p className="text-small">Based on your date of birth entered at the time of account creation.</p>
        </div>
        <div className="form-group">
          <label htmlFor="major">Major:</label>
          <input type="text" id="major" name="major" value={major} onChange={handleMajorChange} />
        </div>
        <div className="form-group">
          <label htmlFor="hobbies">Hobbies:</label>
          <input type="text" id="hobbies" name="hobbies" value={hobbies} onChange={handleHobbiesChange} />
        </div>
        <div className="form-group">
          <label htmlFor="careerAspirations">Career Aspirations:</label>
          <input type="text" id="careerAspirations" name="careerAspirations" value={careerAspirations} onChange={handleCareerAspirationsChange} />
        </div>
        <div className="form-group">
          <button type="submit" className="btn btn-primary">Save Changes</button>
        </div>
      </form>
    </div>
  );
}


export default Profile;
